ie_produit_id_idx` (`category_produit_id`),   CONSTRAINT `categorie_produit_id` FOREIGN KEY (`category_produit_id`) REFERENCES `categorie_produit` (`categorie_produit_id`) ON DELETE NO ACTION ON UPDATE NO ACTION ) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COMMENT='table des articles & produits';CREATE TABLE `filiale` (   `filiale_id` int(10) NOT NULL AUTO_INCREMENT,   `nom` varchar(250) NOT NULL,   `email` varchar(250) NOT NULL,   `siteweb` varchar(250) NOT NULL,   PRIMARY KEY (`filiale_id`),   UNIQUE KEY `siteweb_UNIQUE` (`siteweb`),   UNIQUE KEY `email_UNIQUE` (`email`),   UNIQUE KEY `filiale_id_UNIQUE` (`filiale_id`) ) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;CREATE TABLE `supermarche` (   `supermarche_id` int(11) NOT NULL AUTO_INCREMENT,   `Filiale_id` int(11) NOT NULL,   `nom` varchar(450) NOT NULL,   `telephone` int(50) NOT NULL,   `email` varchar(450) NOT NULL,   PRIMARY KEY (`supermarche_id`),   UNIQUE KEY `supermarche_id_UNIQUE` (`supermarche_id`),   UNIQUE KEY `Filiale_id_UNIQUE` (`Filiale_id`),   UNIQUE KEY `telephone_UNIQUE` (`telephone`),   UNIQUE KEY `email_UNIQUE` (`email`),   CONSTRAINT `fk_filiae_id` FOREIGN KEY (`Filiale_id`) REFERENCES `filiale` (`filiale_id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;CREATE TABLE `adress_utilisateur` (   `adress_utilisateur_id` int(11) NOT NULL AUTO_INCREMENT,   `utilisateur_id` int(11) NOT NULL,   `adress_id` int(11) NOT NULL,   PRIMARY KEY (`adress_utilisateur_id`),   UNIQUE KEY `adress_utilisateur_id_UNIQUE` (`adress_utilisateur_id`),   UNIQUE KEY `utilisateur_id_UNIQUE` (`utilisateur_id`),   UNIQUE KEY `adress_id_UNIQUE` (`adress_id`),   CONSTRAINT `fk_adres_id` FOREIGN KEY (`adress_id`) REFERENCES `adress` (`adress_id`) ON DELETE CASCADE ON UPDATE CASCADE,   CONSTRAINT `fk_utilisateur_id` FOREIGN KEY (`utilisateur_id`) REFERENCES `utilisateur` (`utilisateur_id`) ON DELETE CASCADE ON UPDATE CASCADE ) EN