on_id`),   UNIQUE KEY `livraison_id_UNIQUE` (`livraison_id`),   UNIQUE KEY `NumeroLivraison_UNIQUE` (`NumeroLivraison`),   KEY `fk_livreur_id_idx` (`livreur_id`),   KEY `fk__commande_id_idx` (`commande_id`),   CONSTRAINT `fk__commande_id` FOREIGN KEY (`commande_id`) REFERENCES `commande` (`commande_id`) ON DELETE CASCADE ON UPDATE CASCADE,   CONSTRAINT `fk_livreur_id` FOREIGN KEY (`livreur_id`) REFERENCES `livreur` (`livreur_id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;CREATE TABLE `facture` (   `facture_id` int(11) NOT NULL AUTO_INCREMENT,   `livraison_id` int(11) NOT NULL,   `client_id` int(11) NOT NULL,   `NumeroFacture` varchar(600) NOT NULL,   `total` double NOT NULL DEFAULT '0',   `tarif` varchar(360) NOT NULL,   `tva` int(11) NOT NULL,   PRIMARY KEY (`facture_id`),   UNIQUE KEY `facture_id_UNIQUE` (`facture_id`),   UNIQUE KEY `client_id_UNIQUE` (`client_id`),   UNIQUE KEY `NumeroFacture_UNIQUE` (`NumeroFacture`),   KEY `fk__livraison_id` (`livraison_id`),   CONSTRAINT `fk__livraison_id` FOREIGN KEY (`livraison_id`) REFERENCES `livraison` (`livraison_id`) ON DELETE CASCADE ON UPDATE CASCADE,   CONSTRAINT `fk_client_id` FOREIGN KEY (`client_id`) REFERENCES `client` (`client_id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;CREATE TABLE `commande_line` (   `commande_line_id` int(11) NOT NULL AUTO_INCREMENT,   `Commande_id` int(11) NOT NULL,   `produit_id` int(11) NOT NULL,   `prix` double NOT NULL,   `quantityCommande` int(11) NOT NULL,   `totalLine` double NOT NULL,   PRIMARY KEY (`commande_line_id`),   UNIQUE KEY `commande_line_id_UNIQUE` (`commande_line_id`),   KEY `fk_produit_id_idx` (`produit_id`),   KEY `fk_commande_id_idx` (`Commande_id`),   CONSTRAINT `fk_commande____id` FOREIGN KEY (`Commande_id`) REFERENCES `commande` (`commande_id`) ON DELETE CASCADE ON UPDATE CASCADE,   CONSTRAINT `fk_produit___id` FOREIGN KEY (`produit_id`) REFERENCES `produit` (`produit_id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;CREATE TABLE `livraison_line` (   `livraison_line_id` int(11) NOT NULL AUTO_INCREMENT,   `livraison_id` int(11) NOT NULL,   `produit_id` int(11) NOT NULL,   `quantitylivre` int(11) DEFAULT NULL,   `volume` int(11) DEFAULT NULL,   PRIMARY KEY (`livraison_line_id`),   UNIQUE KEY `livraison_line_id_UNIQUE` (`livraison_line_id`),   KEY `fk_produit_id_idx` (`produit_id`),   KEY `fk__Livraion_line_id_idx` (`livraison_id`),   CONSTRAINT `fk__Livraion_line_id` FOREIGN KEY (`livraison_id`) REFERENCES `livraison` (`livraison_id`) ON DELETE CASCADE ON UPDATE CASCADE,   CONSTRAINT `fk_produit_id` FOREIGN KEY (`produit_id`) REFERENCES `produit` (`produit_id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;CREATE TABLE `facture_line` (   `facture_line_id` int(11) NOT NULL AUTO_INCREMENT,   `facture_id` int(11) NOT NULL,   `produit_id` int(11) NOT NULL,   `prix` double NOT NULL,   `quantityFacture` int(11) NOT NULL,   `totalLine` double NOT NULL,   PRIMARY KEY (`facture_line_id`),   UNIQUE KEY `facture_line_id_UNIQUE` (`facture_line_id`),   KEY `fk__facture_id` (`facture_id`),   KEY `fk__produit_id` (`produit_id`),   CONSTRAINT `fk__facture_id` FOREIGN KEY (`facture_id`) REFERENCES `facture` (`facture_id`) ON DELETE CASCADE ON UPDATE CASCADE,   CONSTRAINT `fk__produit_id` FOREIGN KEY (`produit_id`) REFERENCES `produit` (`produit_id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;